mapper.select:
SELECT mh.HISTORY_ID AS HISTORY_ID, mh.ASSIGNMENT_TYPE AS ASSIGNMENT_TYPE, TO_CHAR(mh.CHANGE_DATE, 'YYYY-MM-DD') AS CHANGE_DATE, mh.MEMBER_ID AS MEMBER_ID, m.NAME AS NAME, mh.OLD_GRADE AS OLD_GRADE, mh.NEW_GRADE AS NEW_GRADE, mh.OLD_DEPT AS OLD_DEPT, mh.NEW_DEPT AS NEW_DEPT, mh.ETC AS ETC, TO_CHAR(mh.CREATE_AT, 'YYYY-MM-DD') AS CREATE_AT FROM MEMBER_HISTORY mh LEFT JOIN MEMBER m ON mh.member_id = m.member_id WHERE 1=1
AND mh.change_date >= #{startDate}
AND mh.change_date <= #{endDate}
AND mh.assignment_type = #{appointGubun}
ORDER BY mh.CREATE_AT DESC

mapper.insert:
INSERT INTO MEMBER_HISTORY (
MEMBER_ID,
ASSIGNMENT_TYPE, OLD_DEPT, NEW_DEPT, OLD_GRADE, NEW_GRADE,
CHANGE_DATE, ETC
) VALUES (
#{MEMBER_ID}, #{ASSIGNMENT_TYPE},
#{OLD_DEPT}, #{NEW_DEPT}, #{OLD_GRADE},
#{NEW_GRADE}, #{CHANGE_DATE},
#{ETC}
)

mapper.update:
UPDATE MEMBER
SET USE_YN = 'N', RESIGN_DATE =
#{CHANGE_DATE}
WHERE MEMBER_ID = #{MEMBER_ID}

UPDATE MEMBER
SET
DEPT_ID = #{oldDept},
GRADE_ID =
#{oldGrade}
WHERE MEMBER_ID = #{memberId}
