mapper.select:
SELECT CHECK_IN_DATE AS COMMUTE_CHECK_IN_DATE , CHECK_IN_TIME AS COMMUTE_CHECK_IN_TIME , NVL(CHECK_OUT_TIME, '-') AS COMMUTE_CHECK_OUT_TIME , MEMBER_ID AS COMMUTE_MEMBER_ID FROM COMMUTE WHERE 1 = 1
AND member_id = #{id}
AND CHECK_IN_DATE BETWEEN #{startDate} AND #{endDate}
ORDER BY CHECK_IN_TIME DESC

SELECT c.ATTENDANCE_TEXT , COUNT(c.ATTENDANCE_TEXT) AS PIE_CHART_VALUE FROM ( SELECT b.* FROM ( SELECT a.* , ROW_NUMBER() OVER(PARTITION BY COMMUTE_ID ORDER BY CT_STARTING_DATE DESC) AS ROW_NUM FROM ( SELECT c.COMMUTE_ID AS COMMUTE_ID , c.MEMBER_ID AS MEMBER_ID , c.CHECK_IN_TIME AS CHECK_IN_TIME , NVL(c.CHECK_OUT_TIME, '-') AS CHECK_OUT_TIME , CASE WHEN c.CHECK_IN_TIME > ct.CHECK_IN_TIME THEN '지각' WHEN c.CHECK_OUT_TIME < ct.CHECK_OUT_TIME THEN '조퇴' ELSE '정상출근' END AS ATTENDANCE_TEXT , c.CREATE_DATE AS CREATE_DATE , ct.STARTING_DATE AS CT_STARTING_DATE , ct.CHECK_IN_TIME AS CT_CHECK_IN_TIME , ct.CHECK_OUT_TIME AS CT_CHECK_OUT_TIME FROM COMMUTE c INNER JOIN COMMUTE_TIME ct ON c.CHECK_IN_DATE >= ct.STARTING_DATE WHERE 1 = 1 AND c.member_id = #{id} ORDER BY c.CHECK_IN_DATE DESC ) a ORDER BY CREATE_DATE DESC ) b WHERE 1 = 1 AND ROW_NUM = 1
AND TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
) c GROUP BY ATTENDANCE_TEXT

SELECT COM_TIME_ID AS COM_TIME_ID , MEMBER_ID AS MEMBER_ID , CHECK_IN_TIME AS CHECK_IN_TIME , CHECK_OUT_TIME AS CHECK_OUT_TIME , STARTING_DATE AS STARTING_DATE FROM COMMUTE_TIME WHERE 1 = 1
AND member_id = #{id}
AND STARTING_DATE BETWEEN #{startDate} AND #{endDate}
ORDER BY STARTING_DATE DESC
