mapper.insert:
    INSERT ALL

		INTO BOM (
			  BOM_ID
			, ITEM_ID
			, PARENT_ID
			, BOM_QUANTITY
		)
		VALUES (
			  #{item.BOM_ID}
			, #{item.ITEM_ID, jdbcType=VARCHAR}
			, #{item.PARENT_ID, jdbcType=VARCHAR}
			, 0
		)

	SELECT * FROM DUAL

mapper.delete:
DELETE FROM BOM
 WHERE BOM_ID IN

    #{id}

	 DELETE FROM BOM_DETAIL
     WHERE BOM_ID IN

        #{id}

update.foreach:
UPDATE BOM
   SET 
       BOM_ID = #{row.BOM_ID}
     , ITEM_ID = #{row.ITEM_ID, jdbcType=VARCHAR}
     , PARENT_ID = #{row.PARENT_ID, jdbcType=VARCHAR}
 WHERE BOM_ID = #{row.BOM_ID}

mapper.insert:
    INSERT ALL

		INTO BOM_DETAIL (
