mapper.insert:
INSERT INTO COMMUTE (COMMUTE_ID, MEMBER_ID, CHECK_IN_TIME,  CHECK_IN_DATE, CHECK_OUT_DATE)
VALUES ((SELECT 'C' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(COMMUTE_ID, 2))), 0) + 1, 10, '0') FROM COMMUTE)
, #{memberId, jdbcType=VARCHAR},
        
            
                #{checkInTime, jdbcType=VARCHAR} 
            
            
                TO_CHAR(SYSDATE, 'HH24:MI:SS') 
            
        ,
        
            
                #{checkInDate, jdbcType=VARCHAR} 
            
            
                TO_CHAR(SYSDATE, 'YYYY-MM-DD') 
            
        ,
        
            
                #{checkOutDate, jdbcType=VARCHAR} 
            
            
                TO_CHAR(SYSDATE, 'YYYY-MM-DD') 
            
        
)

mapper.update:
UPDATE COMMUTE
SET CHECK_OUT_TIME = 
    
        
            #{checkOutTime, jdbcType=VARCHAR} 
        
        
            TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') 
        
    
WHERE MEMBER_ID = #{memberId, jdbcType=VARCHAR}
  AND CHECK_OUT_TIME IS NULL
