mapper.select:

	SELECT COUNT(*)	AS COUNT
	  FROM COMMON_CODE_GROUP
	 WHERE GROUP_ID = #{GROUP_ID}

mapper.insert:

	INSERT INTO COMMON_CODE_GROUP (
		  GROUP_ID 
		, GROUP_NAME
		, DESCRIPTION 
		, CREATE_DATE 
	) VALUES (
		  #{GROUP_ID}
		, #{GROUP_NAME}
		, #{DESCRIPTION}
		, CURRENT_DATE
	)

mapper.select:

	SELECT COUNT(*) 
	  FROM COMMON_CODE
	 WHERE GROUP_ID = #{GROUP_ID}

mapper.update:

	UPDATE COMMON_CODE_GROUP 
	   SET 
		   GROUP_NAME = #{GROUP_NAME}
		 , DESCRIPTION = #{DESCRIPTION}
		 , UPDATE_DATE = CURRENT_DATE
	 WHERE GROUP_ID = #{GROUP_ID}

mapper.select:
SELECT CODE_ID , GROUP_ID , CODE_NAME , DESCRIPTION , USE_YN FROM COMMON_CODE WHERE 1 = 1
AND GROUP_ID = #{GROUP_ID}
AND CODE_ID = #{CODE_ID}


	SELECT COUNT(*)	AS COUNT
	  FROM COMMON_CODE
	 WHERE CODE_ID = #{CODE_ID}

mapper.insert:

	INSERT INTO COMMON_CODE (
		  CODE_ID
		, GROUP_ID
		, CODE_NAME
		, DESCRIPTION
		, USE_YN
		, CREATE_DATE
	) VALUES (
		  #{CODE_ID}
		, #{GROUP_ID}
		, #{CODE_NAME}
		, #{DESCRIPTION}
		, #{USE_YN}
		, CURRENT_DATE
	)

mapper.update:

	UPDATE COMMON_CODE 
	   SET 
		   CODE_NAME = #{CODE_NAME}
		 , DESCRIPTION = #{DESCRIPTION}
		 , USE_YN = #{USE_YN}
		 , UPDATE_DATE = CURRENT_DATE
	 WHERE CODE_ID = #{CODE_ID}
