mapper.select:
SELECT d.DEPT_ID, cc.CODE_NAME AS DEPT_NAME FROM DEPT d LEFT JOIN COMMON_CODE cc ON d.DEPT_ID = cc.CODE_ID
AND d.DEPT_ID = #{DEPT_ID}
AND d.PARENT_DEPT_ID IS NULL


SELECT COUNT(*)	AS COUNT
  FROM DEPT
 WHERE DEPT_ID = #{DEPT_ID}

mapper.insert:

INSERT INTO DEPT (
	  DEPT_ID 
	, MEMBER_ID
	, CREATE_DATE
	, USE_YN 
) VALUES (
	  #{DEPT_ID}
	, #{MEMBER_ID}
	, CURRENT_DATE
	, #{USE_YN}
)

mapper.select:

SELECT COUNT(*) 
  FROM DEPT
 WHERE PARENT_DEPT_ID = #{DEPT_ID}


SELECT COUNT(*)	AS COUNT
  FROM DEPT
 WHERE DEPT_ID = #{DEPT_ID}

mapper.insert:

INSERT INTO DEPT (
	  DEPT_ID
	, PARENT_DEPT_ID
	, MEMBER_ID
	, CREATE_DATE
	, USE_YN 
) VALUES (
	  #{DEPT_ID}
	, #{PARENT_DEPT_ID}
	, #{MEMBER_ID}
	, CURRENT_DATE
	, #{USE_YN}
)

mapper.delete:

		DELETE FROM DEPT
    WHERE DEPT_ID = #{DEPT_ID}
    AND PARENT_DEPT_ID IS NOT NULL

mapper.update:

UPDATE DEPT 
   SET 
	   MEMBER_ID = #{MEMBER_ID}
	 , USE_YN = #{USE_YN}
	 , UPDATE_DATE = CURRENT_DATE
 WHERE DEPT_ID = #{DEPT_ID}
