mapper.select:
SELECT PO.PO_ID, TO_CHAR(PO.CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE, C.CLIENT_NAME, C.RESP_NAME FROM PURCHASE_ORDER PO LEFT JOIN CLIENT C ON PO.CLIENT_ID = C.CLIENT_ID WHERE 1 = 1 -- GOODS_ORDER에 존재하지 않는 PO_ID만 선택 AND PO.PO_ID NOT IN (SELECT GO.PO_ID FROM GOODS_ORDER GO)
AND PO.PO_ID = #{po_id}
AND TRUNC(PO.CREATE_DATE) = TO_DATE(#{create_date}, 'YYYY-MM-DD')

mapper.update:

UPDATE PO_DETAIL
SET PO_STATUS = 'Y'
WHERE PODETAIL_ID = #{PODETAIL_ID}
