mapper.insert:
    INSERT ALL

		INTO ITEM (
		  ITEM_ID
		, ITEM_NAME
		, ITEM_TYPE
		, USE_YN
		, ITEM_UNIT
		, ITEM_COST
		, INB_INSP
		, WAREHOUSE_ID
		, SAFETY_INVENTORY
		, DESCRIPTION
	)
	VALUES (
		  #{item.ITEM_ID}
		, #{item.ITEM_NAME}			
		, #{item.ITEM_TYPE}
		, #{item.USE_YN}
		, #{item.ITEM_UNIT}
		, #{item.ITEM_COST}
		, #{item.INB_INSP}
		, #{item.WAREHOUSE_ID}
		, #{item.SAFETY_INVENTORY}
		, #{item.DESCRIPTION, jdbcType=VARCHAR}
	)

	SELECT * FROM DUAL

update.foreach:
UPDATE ITEM
   SET 
       ITEM_ID = #{row.ITEM_ID}
     , ITEM_NAME = #{row.ITEM_NAME}
     , ITEM_TYPE = #{row.ITEM_TYPE}
     , USE_YN = #{row.USE_YN}
     , ITEM_UNIT = #{row.ITEM_UNIT}
     , ITEM_COST = #{row.ITEM_COST}
     , INB_INSP = #{row.INB_INSP}
     , WAREHOUSE_ID = #{row.WAREHOUSE_ID}
     , SAFETY_INVENTORY = #{row.SAFETY_INVENTORY}
     , DESCRIPTION = #{row.DESCRIPTION, jdbcType=VARCHAR}
 WHERE ITEM_ID = #{row.ITEM_ID}

mapper.select:
SELECT count(*) AS COUNT
	FROM ITEM
WHERE ITEM_ID = #{ITEM_ID}

mapper.delete:
DELETE FROM ITEM
 WHERE ITEM_ID IN

    #{id}
