			, #{item.NAME}
			, #{item.NOTE, jdbcType=VARCHAR}
			, #{item.ROUTING_ID, jdbcType=VARCHAR}
			, #{item.USE_YN}
			, #{item.WORKCENTER_ID}
		)

	SELECT * FROM DUAL

mapper.delete:
DELETE FROM WORKCENTER
 WHERE WORKCENTER_ID IN

    #{id}

update.foreach:
        UPDATE WORKCENTER
           SET 
               NAME = #{row.NAME}
             , NOTE = #{row.NOTE, jdbcType=VARCHAR}
             , ROUTING_ID = #{row.ROUTING_ID, jdbcType=VARCHAR}
             , USE_YN = #{row.USE_YN}
			 , MANAGER_ID = #{row.MANAGER_ID, jdbcType=VARCHAR}
         WHERE WORKCENTER_ID = #{row.WORKCENTER_ID}

mapper.select:
SELECT m.MEMBER_ID AS MEMBER_ID , m.NAME AS NAME , cc.CODE_NAME AS DEPT FROM MEMBER m INNER JOIN COMMON_CODE cc ON m.DEPT_ID = cc.CODE_ID WHERE 1 = 1
AND m.DEPT_ID = #{dept_id}
AND m.NAME = #{member_name}

SELECT EQUIPMENT_ID AS EQUIPMENT_ID , EQUIPMENT_NAME AS EQUIPMENT_NAME , EQUIPMENT_STATUS AS EQUIPMENT_STATUS , EQUIPMENT_NOTE AS EQUIPMENT_NOTE , WORKCENTER_ID AS WORKCENTER_ID FROM EQUIPMENT WHERE 1 = 1
AND WORKCENTER_ID = #{workcenter_id}
AND WORKCENTER_ID IS NULL
AND EQUIPMENT_STATUS = #{use_yn}
AND (EQUIPMENT_ID LIKE concat('%', concat(#{searchKeyword}, '%')) OR EQUIPMENT_NAME LIKE concat('%', concat(#{searchKeyword}, '%')))

update.foreach:
    UPDATE EQUIPMENT 
	   SET WORKCENTER_ID = #{item.WORKCENTER_ID}
	 WHERE EQUIPMENT_ID = #{item.EQUIPMENT_ID}

    UPDATE EQUIPMENT 
	   SET WORKCENTER_ID = NULL
	 WHERE EQUIPMENT_ID = #{item}

    UPDATE EQUIPMENT 
	   SET WORKCENTER_ID = NULL
	 WHERE WORKCENTER_ID = #{item}

mapper.select:
SELECT p.PROCESS_ID , p.NAME , p.USE_YN , p.INSPECTION_YN , p.NOTE , (SELECT count(*) FROM PROCESS_ROUTING_MAPPING WHERE PROCESS_ID = p.PROCESS_ID) AS used_cnt FROM PROCESS p WHERE 1 = 1
AND INSPECTION_YN = #{inspection_yn}
AND USE_YN = #{use_yn}
AND (PROCESS_ID LIKE concat('%', concat(#{searchKeyword}, '%')) OR NAME LIKE concat('%', concat(#{searchKeyword}, '%')))

    	SELECT count(*) AS COUNT
		  FROM ${tableName}
		 WHERE ${tableCodeId} = #{codeVal}

