		VALUES (
			  #{item.PROCESS_ID, jdbcType=VARCHAR}
			, #{item.NAME}
			, #{item.USE_YN}
			, 'N'
			, #{item.NOTE, jdbcType=VARCHAR}
		)

	SELECT * FROM DUAL

update.foreach:
UPDATE PROCESS
   SET 
       NAME = #{row.NAME}
     , USE_YN = #{row.USE_YN}
     , INSPECTION_YN = #{row.INSPECTION_YN}
     , NOTE = #{row.NOTE, jdbcType=VARCHAR}
 WHERE PROCESS_ID = #{row.PROCESS_ID}

