mapper.insert:
    INSERT ALL

		INTO ROUTING (
			  ROUTING_ID
			, NAME
			, USE_YN
			, ITEM_ID
			, NOTE
			, PROCESS_QUANTITY
		)
		VALUES (
			  #{item.ROUTING_ID}
			, #{item.NAME}
			, #{item.USE_YN}
			, #{item.ITEM_ID}
			, #{item.NOTE, jdbcType=VARCHAR}
			, 0
		)

	SELECT * FROM DUAL

update.foreach:
UPDATE ROUTING
   SET 
       NAME = #{row.NAME}
     , USE_YN = #{row.USE_YN}
     , NOTE = #{row.NOTE, jdbcType=VARCHAR}
 WHERE ROUTING_ID = #{row.ROUTING_ID}

mapper.delete:
DELETE FROM ROUTING
 WHERE ROUTING_ID IN

    #{id}

DELETE FROM PROCESS_ROUTING_MAPPING
 WHERE ROUTING_ID IN

    #{id}

mapper.insert:














    INSERT ALL

		INTO PROCESS_ROUTING_MAPPING (
			  PROCESS_ID
			, ROUTING_ID
			, SORT_ORDER
			, IS_LAST_ORDER
		)
		VALUES (
			  #{item.PROCESS_ID}
			, #{item.ROUTING_ID}
			, #{item.SORT_ORDER}
			, #{item.IS_LAST_ORDER}
		)

	SELECT * FROM DUAL

