mapper.insert:
    INSERT ALL

		INTO WORKCENTER (
			  MANAGER_ID
			, NAME
			, NOTE
			, ROUTING_ID
			, USE_YN
			, WORKCENTER_ID
		)
		VALUES (
			  #{item.MANAGER_ID, jdbcType=VARCHAR}
			, #{item.NAME}
			, #{item.NOTE, jdbcType=VARCHAR}
			, #{item.ROUTING_ID, jdbcType=VARCHAR}
			, #{item.USE_YN}
			, #{item.WORKCENTER_ID}
		)

	SELECT * FROM DUAL

mapper.delete:
DELETE FROM WORKCENTER
 WHERE WORKCENTER_ID IN

    #{id}

update.foreach:
        UPDATE WORKCENTER
           SET 
               WORKCENTER_ID = #{row.WORKCENTER_ID}
             , NAME = #{row.NAME}
             , NOTE = #{row.NOTE, jdbcType=VARCHAR}
             , ROUTING_ID = #{row.ROUTING_ID, jdbcType=VARCHAR}
             , USE_YN = #{row.USE_YN}
			 , MANAGER_ID = #{row.MANAGER_ID, jdbcType=VARCHAR}
         WHERE WORKCENTER_ID = #{row.WORKCENTER_ID}

mapper.select:
    	SELECT count(*) AS COUNT
		  FROM WORKCENTER
		 WHERE WORKCENTER_ID = #{WORKCENTER_ID}

SELECT m.MEMBER_ID AS MEMBER_ID , m.NAME AS NAME , cc.CODE_NAME AS DEPT FROM MEMBER m INNER JOIN COMMON_CODE cc ON m.DEPT_ID = cc.CODE_ID WHERE 1 = 1
AND m.DEPT_ID = #{dept_id}
AND m.NAME = #{member_name}

SELECT EQUIPMENT_ID AS EQUIPMENT_ID , EQUIPMENT_NAME AS EQUIPMENT_NAME , EQUIPMENT_STATUS AS EQUIPMENT_STATUS , EQUIPMENT_NOTE AS EQUIPMENT_NOTE FROM EQUIPMENT WHERE 1 = 1
AND WORKCENTER_ID = #{workcenter_id}
AND WORKCENTER_ID IS NULL
