mapper.update:
UPDATE MEMBER
NAME = #{name}, ACCOUNT = #{account}, BANK_CODE = #{bankCode}, ADDRESS_NUM = #{addressNum}, ADDRESS1 = #{address1}, ADDRESS2 = #{address2}, EMAIL = #{email}, PROFILE_PIC = #{profilePic}, USE_YN = #{useYn}, CERTIFICATE = #{certificate}, EDUCATION = #{education}
WHERE MEMBER_ID = #{id}

UPDATE MEMBER
SET CERTIFICATE = #{certificate},
EDUCATION = #{education}
WHERE MEMBER_ID = #{id}

UPDATE MEMBER
SET PASSWD = #{newPassword}
WHERE
MEMBER_ID = #{id}
