mapper.select:

	SELECT COUNT(*)	AS COUNT
	  FROM MES_COMMON_GROUP
	 WHERE MAIN_ID = #{MAIN_ID}

mapper.insert:

	INSERT INTO MES_COMMON_GROUP (
		  MAIN_ID 
		, MAIN_NAME
		, MAIN_ORDER
		, DESCRIPTION 
		, CREATE_DATE 
	) VALUES (
		  #{MAIN_ID}
		, #{MAIN_NAME}
		, #{MAIN_ORDER}
		, #{DESCRIPTION}
		, CURRENT_DATE
	)

mapper.select:
    
    	SELECT COUNT(*) AS COUNT
		  FROM MES_COMMON_GROUP
		  WHERE MAIN_ORDER = #{MAIN_ORDER}


	SELECT COUNT(*) 
	  FROM MES_COMMON
	 WHERE MAIN_ID = #{MAIN_ID}

mapper.update:

	UPDATE MES_COMMON_GROUP 
	   SET 
		   MAIN_NAME = #{MAIN_NAME}
		 , DESCRIPTION = #{DESCRIPTION}
		 , MAIN_ORDER = #{MAIN_ORDER}
		 , UPDATE_DATE = CURRENT_DATE
	 WHERE MAIN_ID = #{MAIN_ID}

mapper.select:
SELECT SUB_ID , MAIN_ID , SUB_NAME , DESCRIPTION , USE_STATUS FROM MES_COMMON WHERE 1 = 1
