mapper.select:
WITH OrderDetails AS ( SELECT ORDER_ID, ORDER_STATUS, (SELECT CLIENT_NAME FROM CLIENT C WHERE C.CLIENT_ID = ORDR.CUSTOMER_ID) AS CUSTOMER_ID, (SELECT MATERIAL_NAME FROM MATERIAL M2 WHERE M2.MATERIAL_ID = ORDR.ITEM_ID) AS ITEM_ID, (SELECT MATERIAL_PRICE FROM MATERIAL M2 WHERE M2.MATERIAL_ID = ORDR.ITEM_ID) AS ITEM_PR, TO_CHAR(ORDER_DATE, 'YYYY-MM-DD') AS ORDER_DATE, (SELECT MATERIAL_PRICE FROM MATERIAL M1 WHERE M1.MATERIAL_ID = ORDR.ITEM_ID) AS MATERIAL_PRICE, ORDER_QTY, TO_CHAR(DELIVERY_DATE, 'YYYY-MM-DD') AS DELIVERY_DATE, ITEM_ID AS UNIT, ITEM_ID AS STANDARD, (SELECT NAME FROM MEMBER M1 WHERE M1.MEMBER_ID = ORDR.SALES_REP_ID) AS SALES_REP_ID, (SELECT NAME FROM MEMBER M1 WHERE M1.MEMBER_ID = ORDR.REGIST_BY) AS REGIST_BY, TO_CHAR(UPDATE_DATE, 'YYYY-MM-DD') AS UPDATE_DATE, REMARKS, TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE FROM ORDERS ORDR
AND ORDER_DATE >= #{startDate}
AND TRUNC(ORDER_DATE) <= #{endDate}
AND CUSTOMER_ID = #{customerId}
AND ITEM_ID = #{itemId}
AND ORDER_ID LIKE '%' || #{orderId} || '%'
AND ORDER_STATUS = #{orderStatus}
) SELECT ORDER_ID, ORDER_STATUS, CUSTOMER_ID, ITEM_ID, ITEM_PR, ORDER_DATE, MATERIAL_PRICE * ORDER_QTY AS ORDER_PR, (MATERIAL_PRICE * ORDER_QTY) * 0.1 AS TAX, (MATERIAL_PRICE * ORDER_QTY) + ((MATERIAL_PRICE * ORDER_QTY) * 0.1) AS ORDER_SUM, DELIVERY_DATE, UNIT, STANDARD, SALES_REP_ID, ORDER_QTY, ORDER_QTY AS SHIPPED_QTY, ORDER_QTY AS BACKLOG, REGIST_BY, UPDATE_DATE, REMARKS, CREATE_DATE FROM OrderDetails ORDER BY ORDER_ID DESC

mapper.delete:
DELETE FROM ORDERS
WHERE ORDER_ID IN

    #{orderId}
