mapper.select:
    	SELECT CLIENT_ID, CLIENT_NAME, RESP_NAME
    	FROM CLIENT
    	 WHERE 
		 	   1 = 1
		
		   AND CLIENT_ID = #{client_id}
		
		
		   AND CLIENT_NAME = #{client_name}
		
		
		   AND RESP_NAME = #{resp_name}

SELECT MATERIAL_ID, MATERIAL_NAME, MATERIAL_PRICE, UNIT FROM MATERIAL WHERE 1 = 1
AND MATERIAL_ID = #{material_id}
AND MATERIAL_NAME LIKE '%' || #{material_name} || '%'
