mapper.select:
    	SELECT CLIENT_ID, CLIENT_NAME
    	FROM CLIENT
    	 WHERE 
		 	   1 = 1
		
		   AND CLIENT_ID = #{client_id}
		
		
		   AND CLIENT_NAME = #{client_name}

    	SELECT MATERIAL_ID, MATERIAL_NAME, MATERIAL_PRICE
    	FROM MATERIAL
    	WHERE 
		 	   1 = 1
		
		   AND MATERIAL_ID = #{material_id}
		
		
		   AND MATERIAL_NAME = #{material_name}
