mapper.select:
SELECT S.PAYDAY, S.MEMBER_ID, M.NAME, S.GRADE_ID, M.DEPT_ID, M.SALARY, S.POSITION_BONUS, S.HOLIDAY_BONUS, S.NIGHT_BONUS, S.YEAREND_BONUS, S.FULLSERVICE_BONUS, S.PERFORMANCE_BONUS, (M.SALARY + S.POSITION_BONUS + S.HOLIDAY_BONUS + S.NIGHT_BONUS + S.YEAREND_BONUS + S.FULLSERVICE_BONUS + S.PERFORMANCE_BONUS) AS TOTAL FROM SALARY S JOIN MEMBER M ON S.MEMBER_ID = M.MEMBER_ID WHERE 1 = 1
AND member_id = #{id}
AND PAYDAY BETWEEN #{startDate} AND #{endDate}
ORDER BY PAYDAY DESC

SELECT S.PAYDAY, S.MEMBER_ID, M.NAME, S.GRADE_ID, M.DEPT_ID, M.SALARY, S.POSITION_BONUS, S.HOLIDAY_BONUS, S.NIGHT_BONUS, S.YEAREND_BONUS, S.FULLSERVICE_BONUS, S.PERFORMANCE_BONUS, (M.SALARY + S.POSITION_BONUS + S.HOLIDAY_BONUS + S.NIGHT_BONUS + S.YEAREND_BONUS + S.FULLSERVICE_BONUS + S.PERFORMANCE_BONUS) AS TOTAL FROM SALARY JOIN MEMBER M ON S.MEMBER_ID = M.MEMBER_ID WHERE 1 = 1 AND MEMBER_ID = #{id}
AND PAYDAY BETWEEN #{startDate} AND #{endDate}
ORDER BY PAYDAY DESC

mapper.insert:
 
		 INSERT ALL
            INTO member (member_id, NAME, GRADE_ID, DEPT_ID, SALARY) 
            VALUES (
			  #{member_id}
            , #{NAME}
            , #{GRADE_ID}
            , #{DEPT_ID}
            , #{SALARY})
            INTO salary (payday, member_id, POSITION_BONUS, HOLIDAY_BONUS, NIGHT_BONUS, YEAREND_BONUS, FULLSERVICE_BONUS, PERFORMANCE_BONUS) 
            VALUES (SYSDATE
            , #{member_id}
            , #{POSITION_BONUS}
            , #{HOLIDAY_BONUS}
            , #{NIGHT_BONUS}
            , #{YEAREND_BONUS}
            , #{FULLSERVICE_BONUS}
            , #{PERFORMANCE_BONUS})
        SELECT * FROM dual;

mapper.update:
	UPDATE SALARY 
   	   SET 
   	   	   POSITION_BONUS = #{positionBonus}
       	 , HOLIDAY_BONUS = #{holidayBonus}
       	 , NIGHT_BONUS = #{nightBonus}
       	 , YEAREND_BONUS = #{yearendBonus}
       	 , FULLSERVICE_BONUS = #{fullserviceBonus}
       	 , PERFORMANCE_BONUS = #{performanceBonus}
	WHERE 
		 MEMBER_ID = #{id}
