mapper.insert:
INSERT ALL

	INTO ORDER_INTAKE (
		  ORDER_ID
		, ORDER_DATE
		, STATUS
		, CLIENT_ID
		, ITEM_ID
		, DELIVERY_DATE
		, QUANTITY
		, NOTE
		, PRICE
	)
	VALUES (
		  #{item.ORDER_ID}
		, TO_CHAR(CURRENT_DATE, 'YYYY-MM-DD')
		,'OS-001'
		, #{item.CLIENT_ID}
		, #{item.ITEM_ID}
		, #{item.DELIVERY_DATE}
		, #{item.QUANTITY}
		, #{item.NOTE, jdbcType=VARCHAR}
		, (
			SELECT TO_NUMBER(NVL(ITEM_COST, 0) * #{item.QUANTITY})
			  FROM item
			 WHERE ITEM_ID = #{item.ITEM_ID}
		)
	)

SELECT * FROM DUAL

update.foreach:
UPDATE ORDER_INTAKE 
   SET 
	   DELIVERY_DATE = #{row.DELIVERY_DATE, jdbcType=VARCHAR}
	 , NOTE = #{row.NOTE, jdbcType=VARCHAR}
 WHERE ORDER_ID = #{row.ORDER_ID}

mapper.delete:
DELETE FROM ORDER_INTAKE
 WHERE ORDER_ID IN

	#{id}
